import { Component, Input } from '@angular/core';
import { Annunci } from '../module';

@Component({
  selector: 'app-annunci',
  templateUrl: './annunci.component.html',
  styleUrls: ['./annunci.component.css']
})
export class AnnunciComponent {
@Input() x! : Annunci
n_espandi=false
espandi():boolean{
  this.n_espandi=true
  return false


}
rispondi(rispondi:HTMLInputElement){
      this.x.rispondi.push(rispondi.value)
    }

}
<p>{{x.nome}} {{x.telefono}} {{x.messaggio}}</p>

<button (click) = "espandi()">espandi</button>
<label>rispondi:</label> <input type="text" #rispondi />
<br />
<button (click)="rispondi(x)">rispondi</button>
<br />
